import React, { useEffect, useState } from "react";
import { Container, Row, Col } from "react-bootstrap";
import ProjectCard from "./ProjectCards";
import Particle from "../Particle";
import ReactGA from "react-ga";

// Import project images
import eCom from "../../Assets/Projects/E-com.png";
import jsomBlog from "../../Assets/Projects/jsomBlog.png";
import taskify from "../../Assets/Projects/Taskify.png";
import aF from "../../Assets/Projects/artisteeF.png";
import aA from "../../Assets/Projects/artisteeA.png";
import easyCode from "../../Assets/Projects/easy-code.png";
// Add other project images as needed

function Projects() {
  const [activeCategory, setActiveCategory] = useState("All");

  useEffect(() => {
    ReactGA.pageview(window.location.pathname + window.location.search);
  }, []);

  const projectsData = [
    {
      imgPath: taskify,
      title: "DeepRL for Algorithmic Trading",
      description: "Developed and optimized deep reinforcement learning models...",
      link: "https://github.com/...",
      category: "DevTools" // Specify the category for each project
    },
    {
      imgPath: easyCode,
      title: "Ecommerce Website",
      description: "Developed an e-commerce website using Java Spring Boot...",
      link: "https://github.com/...",
      category: "Projects"
    },
    {
      imgPath: eCom,
      title: "Time Series Analysis",
      description: "I applied time series analysis techniques...",
      link: "https://github.com/...",
      category: "Data Science"
    },
    // ... add other projects here
  ];

  // Create a list of unique categories from the projects data
  const categories = ["All", ...new Set(projectsData.map(project => project.category))];

  // Filter the projects based on the active category
  const filteredProjects = activeCategory === "All"
    ? projectsData
    : projectsData.filter(project => project.category === activeCategory);

  return (
    <Container fluid className="project-section">
      <Particle />
      <Container>
        <h1 className="project-heading">
          My Recent <strong className="purple">Works</strong>
        </h1>
        <p style={{ color: "white" }}>
          Here are a few projects I've worked on recently.
        </p>
        {/* Category buttons */}
        <Row className="category-container" style={{ justifyContent: "center", paddingBottom: "10px" }}>
          {categories.map(category => (
            <button
              key={category}
              onClick={() => setActiveCategory(category)}
              className={`project-category-button ${activeCategory === category ? 'active' : ''}`}
              style={{ marginRight: '5px' }}
            >
              {category}
            </button>
          ))}
        </Row>
        {/* Project cards */}
        <Row style={{ justifyContent: "center", paddingBottom: "10px" }}>
          {filteredProjects.map((project, index) => (
            <Col key={index} md={4} className="project-card">
              <ProjectCard
                imgPath={project.imgPath}
                isBlog={false}
                title={project.title}
                description={project.description}
                link={project.link}
              />
            </Col>
          ))}
        </Row>
      </Container>
    </Container>
  );
}

export default Projects;
